SELECT Proveedores.Cedula, Proveedores.RazonSocial, Bienes.Descripcion, Bienes.Cantidad, Bienes.Precio, Adjudicaciones.Anyo+'CD-'+Adjudicaciones.Numero+'-CDSG' AS CD FROM ((Proveedores INNER JOIN Adjudicaciones ON Proveedores.Cedula = Adjudicaciones.CedulaProveedor)INNER JOIN DetallesAdjudicaciones ON Adjudicaciones.Adjudicacion = DetallesAdjudicaciones.Adjudicacion) INNER JOIN Bienes ON DetallesAdjudicaciones.Bien = Bienes.Linea