SELECT Bienes.Descripcion, Adjudicaciones.Numero, Adjudicaciones.Observacion, Contrataciones.Fecha, Contrataciones.FechaDI, Contrataciones.Solicitudes, Adjudicaciones.OrdenesCompra FROM ((Bienes INNER JOIN DetallesAdjudicaciones ON Bienes.Linea = DetallesAdjudicaciones.Bien) INNER JOIN Adjudicaciones ON Adjudicaciones.Adjudicacion = DetallesAdjudicaciones.Adjudicacion) INNER JOIN Contrataciones ON Adjudicaciones.Numero = Contrataciones.Numero WHERE Contrataciones.Anyo = $P{anyo} ORDER BY Adjudicaciones.Numero ASC